def mask_file(file_path, fake_extension):
    if not os.path.isfile(file_path):
        print("[‚ùå] Error: The specified file does not exist.")
        return

    base_name = os.path.basename(file_path)
    directory = os.path.dirname(file_path)
    original_extension = os.path.splitext(base_name)[1]

    fake_name = base_name.replace(original_extension, f".{fake_extension}{original_extension}")
    fake_path = os.path.join(directory, fake_name)

    try:
        os.rename(file_path, fake_path)
        print(f"[‚úÖ] Success: '{file_path}' has been disguised as '{fake_path}'")
    except Exception as e:
        print(f"[‚ùå] Error: Failed to rename the file. {e}")

